import React, {useState, useEffect} from 'react';
import './App.css';

const URL = "https://api.giphy.com/v1/gifs/search?api_key=zqIAta0tI3xnT63BqfCRC90on6dF2fg8&q=panda&limit=25&offset=0&rating=g&lang=en";


function App() {
  const [gifs, setGifs] = useState([]);

  useEffect(()=>{
    fetch(URL)
      .then(response => response.json())
      .then(response => {
        const data = response.data;
        const images = data.map(item => item.images.original.url)
        setGifs(images);
      })

  },[])


  return (
    <div className="App">
     {
      gifs.map(gif => <img src={gif} />)
     }
    </div>
  );
}

export default App;
