import React, {useState, useEffect} from "react";
import ImageGif from "../ImageGif";
import getGifs from "../../services/getGif";
import useGifs from "../../hooks/useGif";
import "./ListGifs.css"


const ListGifs = ({search}) =>  {
    
    const[loading, gifs] = useGifs({search});


    return(
        <div className="row">
        {loading ? <h2 className="loading">Loading...</h2> : gifs.map(gif => <ImageGif key={gif} src={gif} />)}
        </div>

    )

 
}

export default ListGifs;
