import React, {useState, useEffect} from "react";
import ImageGif from "./ImageGif";
import getGifs from "../services/getGif";


const ListImageGif = ({search}) =>  {
    const [loading, setLoading] = useState(true);
    const [gifs, setGifs] = useState([]);

    useEffect(()=>{
    setLoading(true)
      getGifs(search).then(images => setGifs(images));
      setLoading(false);
    },[search])


    return(
        <div className="row">
        {loading ? "Cargando" : gifs.map(gif => <ImageGif key={gif} src={gif} />)}
        </div>

    )

 
}


export default ListImageGif;
