import { useState, useEffect } from "react";
import getGifs from "../services/getGif";

export default function useGifs({search}){
    const [loading, setLoading] = useState(false);
    const [gifs, setGifs] = useState([]);

    useEffect(()=>{
    setLoading(true)
      getGifs(search)
      .then(images =>{
        setGifs(images)
        setLoading(false);
    });
    },[search])

    return {loading, gifs};

}
